import React,{useState} from "react";
import axios from "axios";
import "./Payment.css";
import "./AddPayment.css";




export default function AddPayment() {
  const[paymentId, setPaymentID]= useState("");
  const[patientName, setName]= useState("");
  const[paymentType, setPaytype]= useState("");
  const[diagnosis, setDiagnosis]= useState("");
  const[hospitalCharge, setHosCharge]= useState("");
  const[doctorFee, setDocFee]= useState("");
  const[entryDate, setDate]= useState("");
  const[Amount, setAmounts]= useState("");



  function calCulateAmount(e){
    document.getElementById("amount").value = price-price*(discount/100);
    setAmounts( price-price*(discount/100));
        e.preventDefault();
  }

  function sendData(e){   
   
        e.preventDefault();

    const newpayment ={
      paymentId,
      patientName,
      paymentType,
      diagnosis,
      hospitalCharge,
      doctorFee,
      entryDate,
      Amount
    }

    axios.post("http://localhost:8070/payment/add",newpayment).then(()=>{
       alert("Payment Added")   
    }).catch((err)=>{
       alert(err)
    })

    
   }


    
  return (
     
    <div className="paycontainer">
      
       <h1>Add New Payment</h1>
    <form onSubmit={sendData}>
    
    
  
      <div className="form-group">
        <label for="patientName">Client Name</label>
        <input required ={true}  type="text" class="form-control" id="patientName"  placeholder="Enter Patient Name"
        onChange={(e)=>{
          setName(e.target.value);
        }} />

        </div>

        <div className="form-group">
        <label for="paymentType">Payment Type</label>
        <input required ={true}  type="text" class="form-control" id="paymentType" placeholder="Enter Payment Type"
        onChange={(e)=>{
          setPaytype(e.target.value);
        }} />

        </div>

        <div className="form-group">
        <label for="diagnosis">Service Type</label>
        <input  required ={true} type="text" class="form-control" id="diagnosis" placeholder="Enter Diagnosis"
        onChange={(e)=>{
          setDiagnosis(e.target.value);
        }} />

        </div>

        <div className="form-group">
        <label for="hospitalCharge"> Price</label>
        <input required ={true} type="text" class="form-control" id="hospitalCharge" placeholder="Enter Hospital Charge"
        onChange={(e)=>{
          if( document.getElementById("hospitalCharge").value<0){
            document.getElementById("Amount").value=0;
          }else if(document.getElementById("discount").value>document.getElementById("price").value){
            alert("Discount Cannot enter higher than price");
          }
            else{
   document.getElementById("amount").value =document.getElementById("price").value-document.getElementById("price").value*(document.getElementById("discount").value/100);
            
          setAmounts(document.getElementById("amount").value);
            }
          setPrice(e.target.value);
        }} />

        </div>

        <div className="form-group">
        <label for="doctorFee">Discount</label>
        <input type="text" class="form-control" id="doctorFee" placeholder="Enter Doctor Fee"
        onChange={(e)=>{
          if( document.getElementById("hospitalCharge").value<0){
            document.getElementById("Amount").value=0;
          }else if(document.getElementById("doctorFee").value>document.getElementById("hospitalCharge").value){
            alert("Discount Cannot enter higher than price");
          }
            else{
  document.getElementById("Amount").value =document.getElementById("hospitalCharge").value-document.getElementById("hospitalCharge").value*(document.getElementById("discount").value/100);
            
          setAmounts(document.getElementById("Amount").value);
            }

          setDiscounts(e.target.value);
        }} />
        
        </div>

      

        <div className="form-group">
        <label for="entryDate">Entry Date</label>
        <input  required ={true} type="text" class="form-control" id="entryDate" placeholder="Enter Entry Date"
        onChange={(e)=>{
          setDate(e.target.value);
        }} />
        
        </div>
        {/* <button className="btn btn-warning" style={{ marginTop: '15px', marginBottom: '15px' }} onClick={calCulateAmount}>
            Calculate Balance
          </button> */}
        <div className="form-group">
        <label for="amount">Amount</label>
        <input type="text" class="form-control" id="amount" placeholder="Enter the Amount"
        onChange={(e)=>{
          setAmounts(e.target.value);
        }} />
        
        </div>


        <button type="submit" class="btn btn-primary">Submit</button>
          
        
      </form>
    </div>
  );
}
